import { appendFile } from 'fs';
import { useState, useRef } from 'react';
import styles from './app.module.css';

const App = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const btnPrevRef = useRef();
  const btnNextRef = useRef();

  function displayButtons() {
    if (currentIndex === 0) {
      btnPrev.current.style.display = 'none';
      btnNext.current.style.display = 'block';
    } else if (currentIndex === 2) {
      btnPrev.current.style.display = 'block';
      btnNext.current.style.display = 'none';
    } else {
      btnPrev.current.style.display = 'block';
      btnNext.current.style.display = 'block';
    }
  }

  return (
    <div className={styles.container}>
      <div className={styles.slideContainer}>
        <div className={styles.slide}>
          <img src="images/img-1.jpeg" alt="image-1" />
        </div>
        <div className={styles.slide}>
          <img src="images/img-2.jpeg" alt="image-2" />
        </div>
        <div className={styles.slide}>
          <img src="images/person-1.jpeg" alt="prerson-1" />
        </div>
      </div>
      <div className={styles.button}>
        <button
          ref={btnPrevRef}
          onClick={() => {
            setCurrentIndex(--currentIndex);
            displayButtons();
          }}
        >
          Prev
        </button>
        <button
          ref={btnNextRef}
          onClick={() => {
            setCurrentIndex(++currentIndex);
            displayButtons();
          }}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default App;
